func:
// load general settings
msg.settings = flow.get('vicki_settings')

// temporarly working object
msg.temp = {
    topicsLora: {}
};

// split topics in arrays
for (let topicLora in msg.settings.topicsLora) {
    msg.temp.topicsLora[topicLora] = msg.settings.topicsLora[topicLora].split('/')
}

// create result object if not excist
if (!msg.result) { msg.result = {} }                            

msg.result.timestamp = new Date().getTime()

// get deviceName and if not excist exit function without any action
msg.temp.deviceName = msg.payload
for (let i in msg.temp.topicsLora.deviceName) {
    if (msg.temp.deviceName[msg.temp.topicsLora.deviceName[i]]) {
        msg.temp.deviceName = msg.temp.deviceName[msg.temp.topicsLora.deviceName[i]]
    }
}
if (typeof(msg.temp.deviceName) == 'string') {
    msg.result.deviceName = msg.temp.deviceName
    msg.settings.topicHomeassistantDeviceGet = msg.settings.topicHomeassistantDeviceStatus + '/vicki/' + msg.result.deviceName + '/get'
    msg.settings.topicHomeassistantDeviceSet = msg.settings.topicHomeassistantDeviceStatus + '/vicki/' + msg.result.deviceName + '/set'
} else {
    msg.reason = 'deviceName not found or not a string'
    return([msg, null, null])                               // when no device name could be found do nothing, only debug output
}
msg.result.loraTopic = msg.topic.substring(0, msg.topic.indexOf(msg.result.deviceName) + msg.result.deviceName.length)

// get prefious result from memory if excist
msg.result.prefiousData = flow.get('loraPayload_' + msg.result.deviceName) || {}

// if deviceUpdate not excist create with value zero
if (!msg.result.prefiousData.deviceUpdate) {                       
    msg.result.prefiousData.deviceUpdate = 0
}

// collect all sensorData from lora message
msg.temp.sensorData = msg.payload
for (let key in msg.temp.topicsLora.sensorData) {
    if (msg.temp.sensorData[msg.temp.topicsLora.sensorData[key]]) {
        msg.temp.sensorData = msg.temp.sensorData[msg.temp.topicsLora.sensorData[key]]
    }
}

// if object excist in sensorData convert it to an string variable
msg.result.sensorData = {}
for (let key in msg.temp.sensorData) {
    if (typeof(msg.temp.sensorData[key]) == 'object') {
        for (let keyObject in msg.temp.sensorData[key]) {
            let keyNew = keyObject[0].toUpperCase() + keyObject.slice(1);
            msg.result.sensorData[key + keyNew] = msg.temp.sensorData[key][keyObject]
        }
    } else {
        msg.result.sensorData[key] = msg.temp.sensorData[key]
    }
}

// round temperature and humidty
if (msg.result.sensorData.relativeHumidity) { msg.result.sensorData.relativeHumidity = Math.round(msg.result.sensorData.relativeHumidity) }
if (msg.result.sensorData.sensorTemperature) { msg.result.sensorData.sensorTemperature = Math.round(msg.result.sensorData.sensorTemperature * 10) / 10 }

// calculate battery status in %
if (msg.result.sensorData.batteryVoltage) {
	msg.result.sensorData.battery = Math.round(100 * (msg.result.sensorData.batteryVoltage - msg.settings.batteryLow) / (msg.settings.batteryFull - msg.settings.batteryLow))
	if (msg.result.sensorData.battery > 100) {
		msg.result.sensorData.battery = 100
	} else if (msg.result.sensorData.battery < 1) {
		msg.result.sensorData.battery = 1
	}
}

// calculate valve status in %
if (msg.result.sensorData.attachedBackplate == true) {
    if (msg.result.sensorData.motorPosition || msg.result.sensorData.motorPosition != 0) {
        msg.result.sensorData.valve_pct = Math.ceil(100 * ((msg.result.sensorData.motorRange - msg.result.sensorData.motorPosition) / msg.result.sensorData.motorRange))
    } else {
        msg.result.sensorData.valve_pct = 100
    }
} else {
    msg.result.sensorData.valve_pct = 100
}

// define operational mode
if (msg.result.sensorData.attachedBackplate) {
    msg.result.sensorData.system_mode = 'heat'
} else {
    msg.result.sensorData.system_mode = 'off'
}

// get best rssi value from all gateways
msg.temp.gatewayData = msg.payload
for (let key in msg.temp.topicsLora.gatewayData) {               // collect all gateways
    if (msg.temp.gatewayData[msg.temp.topicsLora.gatewayData[key]]) {
        msg.temp.gatewayData = msg.temp.gatewayData[msg.temp.topicsLora.gatewayData[key]]
    }
}
for (let i in msg.temp.gatewayData) {                        // get the highest rssi the data is received
    for (let key in msg.temp.gatewayData[i]) {
        if (key.toLowerCase().includes("rssi")) {
            if (!msg.temp.rssi) {
                msg.temp.rssi = msg.temp.gatewayData[i][key]
            } else if (msg.temp.gatewayData[i][key] > msg.result.rssi) {
                msg.temp.rssi = msg.temp.gatewayData[i][key]
            }
        }
    }
}
if (msg.temp.rssi) {
    msg.result.metadata = {
        rssi: msg.temp.rssi
    }
}

// define object for latest status all data
msg.result.newData = {
    deviceUpdate: msg.result.prefiousData.deviceUpdate,
    sensorData: {},
    metaData: {}
}

if(msg.result.prefiousData.downlink) {
    msg.result.newData.downlink = msg.result.prefiousData.downlink
}

// load newData with the prefiousData
for (let key in msg.result.prefiousData.sensorData) {
    msg.result.newData.sensorData[key] = msg.result.prefiousData.sensorData[key]
}

// add or update newData with last values
for (let key in msg.result.sensorData) {
    msg.result.newData.sensorData[key] = msg.result.sensorData[key]
}

// meta data is alway only the last one
if (msg.result.metadata) {
    msg.result.newData.metaData = msg.result.metadata
}

msg.result.newData.loraTopic = msg.result.loraTopic

flow.set('loraPayload_' + msg.result.deviceName, msg.result.newData)

msg.sensorData = {}
for (let key in msg.result.newData.sensorData) {
    if (typeof (msg.result.newData.sensorData[key]) !== 'object') {
        msg.sensorData[key] = msg.result.newData.sensorData[key]
    }
}

msg.metaData = {}
for (let key in msg.result.newData.metaData) {
    if (typeof (msg.result.newData.metaData[key]) !== 'object') {
        msg.metaData[key] = msg.result.newData.metaData[key]
    }
}

var msgDeviceData = {
    settings: msg.settings,
    result: msg.result,
    sensorData: msg.sensorData,
    metaData: msg.metaData,
}

// send to deviceUpdate and sensorData when beyond the time window
if (msg.result.timestamp > msg.result.newData.deviceUpdate + msg.settings.deviceUpdateWindow * 60000) {
    msg.reason = 'update device & sensorData'
    return ([msg, msgDeviceData, msgDeviceData]);
}

// send only to sensorData when beyond the time window
if (msg.result.sensorData.targetTemperature) {
    msg.reason = 'update sensorData'
    return ([msg, null, msgDeviceData]);
}

msg.return = 'no update possible'
return ([msg, null, null]);

//update the timestamp
msg.result.newData.deviceUpdate = msg.result.timestamp
flow.set('loraPayload_' + msg.result.deviceName, msg.result.newData)

// set min and max temperature when this is not available
if(!msg.sensorData.temperatureRangeSettingsMax) {
  msg.sensorData.temperatureRangeSettingsMax = 30                     //default value MClimate
}
if(!msg.sensorData.temperatureRangeSettingsMin) {
  msg.sensorData.temperatureRangeSettingsMin = 5                      //default value MClimate
}

// repeated information for all type of sensors for th device
msg.device = {
  identifiers: [msg.settings.topicHomeassistantDeviceStatus + '_' + msg.result.deviceName],
  manufacturer: msg.settings.manufacturer,
  model: msg.settings.model,
  name: msg.result.deviceName
}
if(msg.sensorData.deviceVersionsSoftware){msg.device.sw_version = msg.sensorData.deviceVersionsSoftware}
if(msg.sensorData.deviceVersionsHardware){msg.device.hw_version = msg.sensorData.deviceVersionsHardware}

// make an array for all sensors
msg.homeassistant = []

msg.homeassistant.push({
  topic: msg.settings.topicHomeassistant + '/climate/' + msg.result.deviceName + '/trv_climate/config',
  payload: {
    device: msg.device,
    object_id: msg.result.deviceName + '_trv_climate',
    unique_id: msg.result.deviceName + '_trv_climate_' + msg.settings.topicHomeassistantDeviceStatus + '',
    device_class: 'temperature',
    name: 'Radiator thermostat',
    modes: ['off', 'heat'],
    max_temp: msg.sensorData.temperatureRangeSettingsMax,
    min_temp: msg.sensorData.temperatureRangeSettingsMin,
    temperature_unit: 'C',
    temp_step: 1,

    json_attributes_topic: msg.settings.topicHomeassistantDeviceGet,

    mode_state_topic: msg.settings.topicHomeassistantDeviceGet,
    mode_state_template: '{{ value_json.system_mode  if value_json.system_mode=="off" else "heat"}}',

    //action_topic: msg.settings.topicHomeassistantDeviceGet,
    //action_template: '{% set values = {"heat":"heating"} %}{{ values[value_json.running_state] }}',

    current_temperature_topic: msg.settings.topicHomeassistantDeviceGet,
    current_temperature_template: '{{ value_json.sensorTemperature }}',

    temperature_state_topic: msg.settings.topicHomeassistantDeviceGet,
    temperature_state_template: '{{ value_json.targetTemperature }}',

    temperature_command_topic: msg.settings.topicHomeassistantDeviceSet + '/targetTemperature',
    mode_command_topic: msg.settings.topicHomeassistantDeviceSet + '/system_mode',
  }
})

msg.homeassistant.push({
  topic: msg.settings.topicHomeassistant + '/number/' + msg.result.deviceName + '/trv_number/config',
  payload: {
    device: msg.device,
    object_id: msg.result.deviceName + '_trv_number',
    unique_id: msg.result.deviceName + '_trv_number_' + msg.settings.topicHomeassistantDeviceStatus + '',
    device_class: 'temperature',
    name: 'Target temperature',
    max: msg.sensorData.temperatureRangeSettingsMax,
    min: msg.sensorData.temperatureRangeSettingsMin,
    unit_of_measurement: '°C',
    step: 1,
    mode: 'slider',

//    json_attributes_topic: msg.settings.topicHomeassistantDeviceGet,
//    json_attributes_template: '{{ value_json.targetTemperature }}',

    state_topic: msg.settings.topicHomeassistantDeviceGet,
    value_template: '{{ value_json.targetTemperature }}',

    command_topic: msg.settings.topicHomeassistantDeviceSet + '/targetTemperature',
  }
})

msg.homeassistant.push({
  topic: msg.settings.topicHomeassistant + '/sensor/' + msg.result.deviceName + '/battery/config',
  payload: {
    device: msg.device,
    object_id: msg.result.deviceName + '_battery',
    unique_id: msg.result.deviceName + '_battery_' + msg.settings.topicHomeassistantDeviceStatus + '',
    enabled_by_default: true,
    state_class: 'measurement',
    device_class: 'battery',
    entity_category: 'diagnostic',
    unit_of_measurement: '%',

    state_topic: msg.settings.topicHomeassistantDeviceGet,
    json_attributes_topic: msg.settings.topicHomeassistantDeviceGet,
    value_template: '{{ value_json.battery }}'
  }
})

msg.homeassistant.push({
  topic: msg.settings.topicHomeassistant + '/sensor/' + msg.result.deviceName + '/rssi/config',
  payload: {
    device: msg.device,
    object_id: msg.result.deviceName + '_rssi',
    unique_id: msg.result.deviceName + '_rssi_' + msg.settings.topicHomeassistantDeviceStatus + '',
    enabled_by_default: false,
    state_class: 'measurement',
    device_class: 'signal_strength',
    entity_category: 'diagnostic',

    state_topic: msg.settings.topicHomeassistantDeviceGet,
    json_attributes_topic: msg.settings.topicHomeassistantDeviceGet,
    value_template: '{{ value_json.rssi }}',
  }
})

msg.homeassistant.push({
  topic: msg.settings.topicHomeassistant + '/sensor/' + msg.result.deviceName + '/valve_pct/config',
  payload: {
    device: msg.device,
    object_id: msg.result.deviceName + '_valve_pct',
    unique_id: msg.result.deviceName + '_valve_pct_' + msg.settings.topicHomeassistantDeviceStatus + '',
    name: 'Valve open status',
    unit_of_measurement: '%',
    icon: 'mdi:pipe-valve',

    state_topic: msg.settings.topicHomeassistantDeviceGet,
    json_attributes_topic: msg.settings.topicHomeassistantDeviceGet,
    value_template: '{{ value_json.valve_pct }}',
  }
})

msg.homeassistant.push({
  topic: msg.settings.topicHomeassistant + '/sensor/' + msg.result.deviceName + '/humidty/config',
  payload: {
    device: msg.device,
    object_id: msg.result.deviceName + '_humidity',
    unique_id: msg.result.deviceName + '_humidity_' + msg.settings.topicHomeassistantDeviceStatus + '',
//    name: 'humidity',
    device_class: 'humidity',
    unit_of_measurement: '%',

    state_topic: msg.settings.topicHomeassistantDeviceGet,
    json_attributes_topic: msg.settings.topicHomeassistantDeviceGet,
    value_template: '{{ value_json.relativeHumidity }}',
  }
})

msg.homeassistant.push({
  topic: msg.settings.topicHomeassistant + '/binary_sensor/' + msg.result.deviceName + '/attachedBackplate/config',
  payload: {
    device: msg.device,
    object_id: msg.result.deviceName + '_attachedBackplate_',
    unique_id: msg.result.deviceName + '_attachedBackplate_' + msg.settings.topicHomeassistantDeviceStatus,
    name: 'Attached backplate',
    device_class: 'connectivity',
    payload_off: false,
    payload_on: true,
    state_topic: msg.settings.topicHomeassistantDeviceGet,
    json_attributes_topic: msg.settings.topicHomeassistantDeviceGet,
    value_template: '{{ value_json.attachedBackplate }}',
  }
})

msg.homeassistant.push({
  topic: msg.settings.topicHomeassistant + '/binary_sensor/' + msg.result.deviceName + '/childLock/config',
  payload: {
    device: msg.device,
    object_id: msg.result.deviceName + '_childLock',
    unique_id: msg.result.deviceName + '_childLock_' + msg.settings.topicHomeassistantDeviceStatus,
    name: 'Child lock',
//    device_class: 'lock',
    icon: 'mdi:lock',
    payload_off: false,
    payload_on: true,
    payload_available: 'on',
    payload_not_available: 'off',
    state_topic: msg.settings.topicHomeassistantDeviceGet,
    json_attributes_topic: msg.settings.topicHomeassistantDeviceGet,
    value_template: '{{ value_json.childLock }}',
  }
})

msg.homeassistant.push({
  topic: msg.settings.topicHomeassistant + '/binary_sensor/' + msg.result.deviceName + '/openWindow/config',
  payload: {
    device: msg.device,
    object_id: msg.result.deviceName + '_openWindow',
    unique_id: msg.result.deviceName + '_openWindow_' + msg.settings.topicHomeassistantDeviceStatus,
    name: 'Open window detection',
    icon: 'mdi:window-closed-variant',
    payload_off: false,
    payload_on: true,
    payload_available: 'on',
    payload_not_available: 'off',
    state_topic: msg.settings.topicHomeassistantDeviceGet,
    json_attributes_topic: msg.settings.topicHomeassistantDeviceGet,
    value_template: '{{ value_json.openWindow }}',
  }
})

// send all sensors one by one to the mqtt broker
for (let i = 0; i < msg.homeassistant.length; i++) {
  let msgMqtt = {
    topic: msg.homeassistant[i].topic,
    payload: msg.homeassistant[i].payload
  }

  node.send([null, msgMqtt]);
}

return [msg, null];

// mqtt message to update sensorData
var msgOut = {
	topic: msg.settings.topicHomeassistantDeviceGet,
    payload: {
	  	device: {
	  		friendlyName: msg.result.deviceName,
	  		ieeeAddr: msg.result.deviceName,
		  	manufacturerName: msg.settings.manufacturer,
		  	model: msg.settings.model,
	  		powerSource: 'Battery',
	  	}
	}
}

// add all sensorData
for(let key in msg.sensorData) {
	msgOut.payload[key] = msg.sensorData[key]
}

// add all metaData
for(let key in msg.metaData) {
	msgOut.payload[key] = msg.metaData[key]
}

return [msg, msgOut];

// load general settings
msg.settings = flow.get('vicki_settings')

// temporarly working object
msg.temp = {
    topicArray: msg.topic.split("/")
}

// create result object if not excist
if (!msg.result) { msg.result = {} }

msg.result.deviceName = msg.temp.topicArray[2]
msg.result.variable = msg.temp.topicArray[4]
msg.result.commandSet = 'set' + msg.result.variable[0].toUpperCase() + msg.result.variable.slice(1)
msg.result.value = msg.payload
msg.result.timestamp = new Date().getTime()

msg.result.topicHomeassistantDeviceGe = msg.settings.topicHomeassistantDeviceStatus + '/vicki/' + msg.result.deviceName + '/get'
msg.result.topicHomeassistantDeviceSet = msg.settings.topicHomeassistantDeviceStatus + '/vicki/' + msg.result.deviceName + '/set'

// get prefious result from memory if excist
msg.result.prefiousData = flow.get('loraPayload_' + msg.result.deviceName) || {}
//msg.result.prefiousData = flow.get('loraPayload_' + msg.result.deviceName) || { timestamp: 0 }

// define object for latest status all data
msg.result.newData = {
    deviceUpdate: msg.result.prefiousData.deviceUpdate,
    sensorData: {},
    metaData: msg.result.prefiousData.metaData,
    loraTopic: msg.result.prefiousData.loraTopic
}

// load sensorData from prefiousData into newData
for (let key in msg.result.prefiousData.sensorData) {
    msg.result.newData.sensorData[key] = msg.result.prefiousData.sensorData[key]
}

// update newData with last values
msg.result.newData.sensorData[msg.result.variable] = msg.result.value

if (msg.result.prefiousData.downlink) {
    msg.result.newData.downlink = msg.result.prefiousData.downlink
} else {
    msg.result.newData.downlink = {
        [msg.result.variable]: msg.result.prefiousData.sensorData[msg.result.variable],
    }
}
msg.result.newData.downlink.commandSet = msg.result.commandSet
msg.result.newData.downlink.variable = msg.result.variable
msg.result.newData.downlink.value = msg.result.value
msg.result.newData.downlink.timestamp = msg.result.timestamp

flow.set('loraPayload_' + msg.result.deviceName, msg.result.newData)

msg.sensorData = {}
for (let key in msg.result.newData.sensorData) {
    if (typeof (msg.result.newData.sensorData[key]) !== 'object') {
        msg.sensorData[key] = msg.result.newData.sensorData[key]
    }
}

msg.metaData = {}
for (let key in msg.result.newData.metaData) {
    if (typeof (msg.result.newData.metaData[key]) !== 'object') {
        msg.metaData[key] = msg.result.newData.metaData[key]
    }
}

var msgDeviceData = {
    settings: msg.settings,
    result: msg.result,
    sensorData: msg.sensorData,
    metaData: msg.metaData,
    downlink: msg.result.newData.downlink,
    timestamp: msg.result.timestamp,
}

return [msg, msgDeviceData, msgDeviceData];

msg.result.prefiousData = flow.get('loraPayload_' + msg.result.deviceName) || {}

if (msg.result.prefiousData.downlink.timestamp == msg.result.timestamp) {
    if (msg.result.value != msg.result.prefiousData.downlink[msg.result.variable]) {
        var msgLora = {
            topic: msg.result.newData.loraTopic + '/down/push',
            payload: {
                downlinks: [
                    {
                        f_port: 1,
                        decoded_payload: {[msg.result.commandSet]: msg.result.value},
                        priority: "NORMAL",
                        confirmed: false
                    }
                ]
            }
        }
        node.send([msg, msgLora])
        msg.exit = 'lora message send'
    } else {
        msg.exit = 'targetTemperature the same'
    }
    msg.result.newData = {}
    for (let key in msg.result.prefiousData) {
        if(key != 'downlink') {
            msg.result.newData[key] = msg.result.prefiousData[key]
        }
    }

    flow.set('loraPayload_' + msg.result.deviceName, msg.result.newData)
} else {
    msg.exit = 'timestamp different'
}

return [msg, null];

// general settings
// topic names which could be different per Lora server
msg.settings = {
    manufacturer: 'MClimate',
    model: 'Vicki - LoRaWAN Smart Radiator Thermostat',
    topicHomeassistantDeviceStatus: 'lora2nodered2mqtt',
    topicHomeassistant: 'homeassistant',
    topicsLora: {
        deviceName: 'end_device_ids/device_id',             // TTN & TTI
        sensorData: 'uplink_message/decoded_payload',       // TTN & TTI
        gatewayData: 'uplink_message/rx_metadata'           // TTN & TTI
    },
    deviceUpdateWindow: 1400,      // in minutes to update device in HA
//    deviceUpdateWindow: 10,      // in minutes
//    deviceUpdateWindow: 1,      // in minutes
    batteryFull: 1.7 * 2,
	batteryLow: 1.3 * 2
}

flow.set('vicki_settings', msg.settings)

return msg;

// one or more deviceNames where the software and temperature range needs to be requested
//msg.deviceNameList = ['device_1', 'device_2', 'device_X']     

msg.deviceNameList = ['eui-70b3d52dd3015cd5', 'eui-70b3d52dd3015c93']

for (let i in msg.deviceNameList) {
    let deviceName = msg.deviceNameList[i]
    var msgLora = {
        topic: 'v3/ha-thermostaat@ttn/devices/' + deviceName + '/down/push',
        payload: {
            downlinks: [
                {
                    f_port: 1,
//                    decoded_payload: { getDeviceVersions: '', getTemperatureRange: '' },
                    decoded_payload: { getDeviceVersions: '', getAllParams: '' },
                    priority: "NORMAL",
                    confirmed: false
                }
            ]
        }
    }
    node.send([null, msgLora]);
}

return [msg, null];
